	function validateFileUpload(){
		$('#photo-upload input[type="submit"]').on('click',function(){
			var filenameSplit = $('.file-name').html().split('.');
			var filetype = filenameSplit[filenameSplit.length-1].toLowerCase();
			if ($('#user-photo').val() == ''){
				$('#user-photo').addClass('error');
				$('.file-error').html('Anda belum memilih foto');
			}
			else if (filetype != 'jpg' && filetype != 'jpeg' && filetype != 'png' && filetype != 'gif'){
				$('#user-photo').addClass('error');
				$('.file-error').html('Tipe file yang Anda pilih tidak valid');
			}
			else if(sizeupload > 1048576){
				$('#user-photo').addClass('error');
				$('.file-error').html('Ukuran file Anda lebih dari 1 Mb');
			}
			else {
				$('#user-photo').removeClass('error');
				$('.file-error').html('');
			}
			//
			if ($('#photo-upload .error').length > 0){
				return false;
			}
		});
	}
	
	
	
