	/* FUNCTIONS FOR PRODUCT LIST */
	
	function productCarousel(){
		var anim = 0;
		var t = 500;
		var easing = 'linear';
		var viewed = 5;
		
		var container = $('.carousel .container');
		var items = $('.carousel .item');
		
		var w = items.width();
		var n = items.length;
		
		if (n > viewed){
			// add nav buttons
			$('.carousel .nav').show();
			
			// assign navigation function
			$('.carousel .next').on('click',function(){
				if (anim == 0){
					anim = 1;
					
					items.each(function(){
						var index = $(this).index();
						if (index > 0 && index <= viewed){
							$(this).removeClass().addClass('item').addClass('viewed').addClass('viewed'+index);
						}
						else {
							$(this).removeClass().addClass('item');
						}
					});
					
					container.animate({'marginLeft':'-='+w},t,function(){
						container.find('.item:first-child').appendTo(container);
						container.css('margin-left',0);
						anim = 0;
					});
				}
			});
			$('.carousel .prev').on('click',function(){
				if (anim == 0){
					anim = 1;
					container.find('.item:last-child').prependTo(container);
					container.css('margin-left',-w);
					
					items.each(function(){
						var index = $(this).index();
						if (index < viewed){
							$(this).removeClass().addClass('item').addClass('viewed').addClass('viewed'+(index+1));
						}
						else {
							$(this).removeClass().addClass('item');
						}
					});
					
					container.animate({'marginLeft':'+='+w},t,easing,function(){
						anim = 0;
					});
				}
			});
		}
	}
	
	
	
	
	
