function centerImages(imgContainer){
	var winW = $(window).width();
	var winH = $(window).height();
	var imgW;
	var imgH;
	var img;
	var boxW;
	var boxH;
	$(imgContainer).each(function(){
		$(this).append('<div class="img-loading"></div>');
	});
	$(imgContainer).each(function(){
		var src = $(this).find('> img').attr('src');
		$(this).find('> img').attr('src','');
		$(this).find('> img').attr('src',src);
		$(this).find('> img').load(function(){
			var img = $(this);
			img.css({'width':'auto','height':'auto'});
			var imgW = img.width();
			var imgH = img.height();
			var boxW = $(this).parent().width();
			var boxH = $(this).parent().height();
			if (imgW/imgH > boxW/boxH){
				img.css({'height':'100%','width':'auto'});
				imgW = img.width();
				img.css({'margin-top':0,'margin-left':(boxW-imgW)/2});
			}
			else {
				img.css({'width':'100%','height':'auto'});
				imgH = img.height();
				img.css({'margin-left':0,'margin-top':(boxH-imgH)/2});
			}
			img.parent().find('.img-loading').remove();
		});
	});
}

